```java
public V get(Object key) {
    // null
    Object k = maskNull(key);
    // keyhash
    int h = hash(k);
    // table
    Entry<K,V>[] tab = getTable();
    // 
    int index = indexFor(h, tab.length);
    Entry<K,V> e = tab[index];
    // 
    while (e != null) {
        if (e.hash == h && eq(k, e.get()))
            return e.value;
        e = e.next;
    }
    return null;
}
