public V remove(Object key) {
    // null
    Object k = maskNull(key);
    // keyhash
    int h = hash(k);
    // table
    Entry<K,V>[] tab = getTable();
    // 
    int i = indexFor(h, tab.length);
    Entry<K,V> prev = tab[i];
    Entry<K,V> e = prev;

    while (e != null) {
        Entry<K,V> next = e.next;
        // Entry
        if (h == e.hash && eq(k, e.get())) {
            modCount++;
            size--;
            if (prev == e)
                tab[i] = next;
            else
                prev.next = next;
            // Entryvalue
            return e.value;
        }
        prev = e;
        e = next;
    }

    return null;
}
